// Store data
chrome.storage.local.set({ key: "value" }).then(() => {});

// Listen for changes
chrome.storage.onChanged.addListener((changes, namespace) => {
  for (let [key, { oldValue, newValue }] of Object.entries(changes)) {
}
});

// Get current active tab
async function getCurrentTab() {
  let queryOptions = { active: true, lastFocusedWindow: true };
  let [tab] = await chrome.tabs.query(queryOptions);
  return tab;
}

// Create a new tab
chrome.tabs.create({
  url: "https://example.com"
});

// Create a bookmark folder
chrome.bookmarks.create(
  { parentId: bookmarkBar.id, title: "Extension bookmarks" },
  function(newFolder) {
  }
);

// Create a bookmark
chrome.bookmarks.create({
  parentId: extensionsFolderId,
  title: "Extensions doc",
  url: "https://developer.chrome.com/docs/extensions"
});

// Search bookmarks
chrome.bookmarks.search("example", (results) => {
});

// Create a basic notification
chrome.notifications.create("notification-id", {
  type: "basic",
  iconUrl: "./icon.png",
  title: "Notification Title",
  message: "This is the notification message",
  priority: 2
});

// Listen for notification clicks
chrome.notifications.onClicked.addListener((notificationId) => {
});

// Get OAuth2 token (requires oauth2 configuration in manifest)
chrome.identity.getAuthToken({ interactive: true }, function(token) {
  if (chrome.runtime.lastError) {
    return;
  }

  // Use token to make API requests
  fetch("https://www.googleapis.com/oauth2/v1/userinfo?alt=json", {
    headers: { Authorization: "Bearer " + token }
  })
  .then(response => response.json())
  .then(data => {});
});

// Execute script in current tab
chrome.action.onClicked.addListener((tab) => {
  chrome.scripting.executeScript({
    target: { tabId: tab.id },
    func: () => {
      document.body.style.backgroundColor = "lightblue";
    }
  });
});

// Execute script from file
chrome.scripting.executeScript({
  target: { tabId: tabId },
  files: ["content.js"]
});

// Insert CSS
chrome.scripting.insertCSS({
  target: { tabId: tabId },
  files: ["styles.css"]
});

// Create an alarm
chrome.runtime.onInstalled.addListener(async ({ reason }) => {
  if (reason !== "install") return;

  await chrome.alarms.create("demo-alarm", {
    delayInMinutes: 1,
    periodInMinutes: 1
  });
});

// Listen for alarm
chrome.alarms.onAlarm.addListener((alarm) => {
  // Perform periodic task
});

// Get existing alarm
const alarm = await chrome.alarms.get("demo-alarm");

// Create context menu item
chrome.runtime.onInstalled.addListener(() => {
  chrome.contextMenus.create({
    id: "search-google",
    title: "Search Google for '%s'",
    contexts: ["selection"]
  });
});

// Handle context menu click
chrome.contextMenus.onClicked.addListener((info, tab) => {
  if (info.menuItemId === "search-google") {
    const searchUrl = `https://www.google.com/search?q=${encodeURIComponent(info.selectionText)}`;
    chrome.tabs.create({ url: searchUrl });
  }
});

// Search history
chrome.history.search(
  { text: "example", maxResults: 10 },
  (results) => {
    results.forEach((page) => {
    });
  }
);

// Add URL to history
chrome.history.addUrl({ url: "https://example.com" });

// Delete URL from history
chrome.history.deleteUrl({ url: "https://example.com" });

// Listen for visits
chrome.history.onVisited.addListener((result) => {
});

// Observe requests (non-blocking)
chrome.webRequest.onBeforeRequest.addListener(
  (details) => {
  },
  { urls: ["<all_urls>"] }
);

// Monitor response headers
chrome.webRequest.onHeadersReceived.addListener(
  (details) => {
  },
  { urls: ["<all_urls>"] },
  ["responseHeaders"]
