// Query tab groups
const groups = await chrome.tabGroups.query({ 
  title: "My Group" 
});

// Update tab group
await chrome.tabGroups.update(groupId, {
  title: "Updated Group",
  color: "blue",
  collapsed: false
});

// Group tabs together
