api_docs.code_example:
// Create a payment intent
const response = await fetch('https://api.stripe.com/v1/payment_intents', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/x-www-form-urlencoded'
  },
  body: new URLSearchParams({
    amount: '2000',
    currency: 'usd',
    payment_method_types: 'card'
  })
});
const data = await response.json();

// Chat completion
const response = await fetch('https://api.openai.com/v1/chat/completions', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    model: 'gpt-4',
    messages: [
      { role: 'user', content: 'Hello!' }
    ]
  })
});
const data = await response.json();

// Get user repositories
const response = await fetch('https://api.github.com/user/repos', {
  headers: {
    'Authorization': `Bearer ${token}`,
    'Accept': 'application/vnd.github.v3+json'
  }
});
const repos = await response.json();

// Send SMS
const credentials = btoa(`${accountSid}:${authToken}`);
const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${accountSid}/Messages.json`, {
  method: 'POST',
  headers: {
    'Authorization': `Basic ${credentials}`,
    'Content-Type': 'application/x-www-form-urlencoded'
  },
  body: new URLSearchParams({
    To: '+1234567890',
    From: '+0987654321',
    Body: 'Hello from Twilio!'
  })
});
const data = await response.json();

// Send email
const response = await fetch('https://api.sendgrid.com/v3/mail/send', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    personalizations: [{
      to: [{ email: 'recipient@example.com' }],
      subject: 'Hello from SendGrid'
    }],
    from: { email: 'sender@example.com' },
    content: [{
      type: 'text/plain',
      value: 'Hello World!'
    }]
  })
});

// Get current weather
const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=London&appid=${apiKey}&units=metric`);
const data = await response.json();
// data.main.temp contains temperature in Celsius

// Translate text
const response = await fetch(`https://translation.googleapis.com/language/translate/v2?key=${apiKey}`, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    q: 'Hello, world!',
    target: 'es',
    source: 'en'
  })
});
const data = await response.json();

// Get stock quote
const response = await fetch(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=AAPL&apikey=${apiKey}`);
const data = await response.json();

// Search for tracks
const response = await fetch('https://api.spotify.com/v1/search?q=artist:beatles&type=track&limit=10', {
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  }
});
const data = await response.json();

// Get user by username
const response = await fetch('https://api.twitter.com/2/users/by/username/elonmusk', {
  headers: {
    'Authorization': `Bearer ${bearerToken}`,
    'Content-Type': 'application/json'
  }
});
const data = await response.json();

// Search for videos
const response = await fetch(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=javascript&key=${apiKey}&maxResults=10`);
const data = await response.json();

// Geocode an address
const response = await fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=1600+Amphitheatre+Parkway,+Mountain+View,+CA&key=${apiKey}`);
const data = await response.json();
