api_docs.code_example:
// Create a payment intent
const response = await fetch('https://api.stripe.com/v1/payment_intents', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/x-www-form-urlencoded'
  },
  body: new URLSearchParams({
    amount: '2000',
    currency: 'usd',
    payment_method_types: 'card'
  })
});
const data = await response.json();

// Chat completion
const response = await fetch('https://api.openai.com/v1/chat/completions', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    model: 'gpt-4',
    messages: [
      { role: 'user', content: 'Hello!' }
    ]
  })
});
const data = await response.json();

// Get user repositories
const response = await fetch('https://api.github.com/user/repos', {
  headers: {
    'Authorization': `Bearer ${token}`,
    'Accept': 'application/vnd.github.v3+json'
  }
});
const repos = await response.json();

// Send SMS
const credentials = btoa(`${accountSid}:${authToken}`);
const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${accountSid}/Messages.json`, {
  method: 'POST',
  headers: {
    'Authorization': `Basic ${credentials}`,
    'Content-Type': 'application/x-www-form-urlencoded'
  },
  body: new URLSearchParams({
    To: '+1234567890',
    From: '+0987654321',
    Body: 'Hello from Twilio!'
  })
});
const data = await response.json();

// Send email
const response = await fetch('https://api.sendgrid.com/v3/mail/send', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    personalizations: [{
      to: [{ email: 'recipient@example.com' }],
      subject: 'Hello from SendGrid'
    }],
    from: { email: 'sender@example.com' },
    content: [{
      type: 'text/plain',
      value: 'Hello World!'
    }]
  })
});

// Get current weather
const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=London&appid=${apiKey}&units=metric`);
const data = await response.json();
// data.main.temp contains temperature in Celsius

// Translate text
const response = await fetch(`https://translation.googleapis.com/language/translate/v2?key=${apiKey}`, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    q: 'Hello, world!',
    target: 'es',
    source: 'en'
  })
});
const data = await response.json();

// Get stock quote
const response = await fetch(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=AAPL&apikey=${apiKey}`);
const data = await response.json();

// Search for tracks
const response = await fetch('https://api.spotify.com/v1/search?q=artist:beatles&type=track&limit=10', {
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  }
});
const data = await response.json();

// Get user by username
const response = await fetch('https://api.twitter.com/2/users/by/username/elonmusk', {
  headers: {
    'Authorization': `Bearer ${bearerToken}`,
    'Content-Type': 'application/json'
  }
});
const data = await response.json();

// Search for videos
const response = await fetch(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=javascript&key=${apiKey}&maxResults=10`);
const data = await response.json();

// Geocode an address
const response = await fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=1600+Amphitheatre+Parkway,+Mountain+View,+CA&key=${apiKey}`);
const data = await response.json();

// Send an email
const emailContent = [
  'From: sender@example.com',
  'To: recipient@example.com',
  'Subject: Hello from Gmail API',
  '',
  'This is the email body.'
].join('\n');

const encodedEmail = btoa(emailContent).replace(/\+/g, '-').replace(/\//g, '_').replace(/=+$/, '');

const response = await fetch('https://gmail.googleapis.com/gmail/v1/users/me/messages/send', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({ raw: encodedEmail })
});
const result = await response.json();

// List recent messages
const listResponse = await fetch('https://gmail.googleapis.com/gmail/v1/users/me/messages?maxResults=10', {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const messages = await listResponse.json();

// List files from Drive
const response = await fetch('https://www.googleapis.com/drive/v3/files?pageSize=10&fields=files(id,name,mimeType)', {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const files = await response.json();

// Create a new folder
const createResponse = await fetch('https://www.googleapis.com/drive/v3/files', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    name: 'My New Folder',
    mimeType: 'application/vnd.google-apps.folder'
  })
});
const newFolder = await createResponse.json();

// Search for files
const searchResponse = await fetch(`https://www.googleapis.com/drive/v3/files?q=name contains 'document'`, {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const searchResults = await searchResponse.json();

// Create a calendar event
const event = {
  summary: 'Team Meeting',
  description: 'Weekly team sync',
  start: {
    dateTime: '2025-11-25T10:00:00-07:00',
    timeZone: 'America/Los_Angeles'
  },
  end: {
    dateTime: '2025-11-25T11:00:00-07:00',
    timeZone: 'America/Los_Angeles'
  },
  attendees: [
    { email: 'colleague@example.com' }
  ]
};

const response = await fetch('https://www.googleapis.com/calendar/v3/calendars/primary/events', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify(event)
});
const createdEvent = await response.json();

// List upcoming events
const listResponse = await fetch('https://www.googleapis.com/calendar/v3/calendars/primary/events?timeMin=' + new Date().toISOString(), {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const events = await listResponse.json();

// Read values from a sheet
const spreadsheetId = 'your-spreadsheet-id';
const range = 'Sheet1!A1:D10';

const response = await fetch(`https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${range}`, {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const data = await response.json();
const rows = data.values; // Array of arrays

// Write values to a sheet
const updateResponse = await fetch(`https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${range}?valueInputOption=RAW`, {
  method: 'PUT',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    values: [
      ['Name', 'Email', 'Status'],
      ['John Doe', 'john@example.com', 'Active'],
      ['Jane Smith', 'jane@example.com', 'Active']
    ]
  })
});
const updateResult = await updateResponse.json();

// Create a new Google Doc
const createResponse = await fetch('https://docs.googleapis.com/v1/documents', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    title: 'My New Document'
  })
});
const newDoc = await createResponse.json();

// Insert text into document
const updateResponse = await fetch(`https://docs.googleapis.com/v1/documents/${newDoc.documentId}:batchUpdate`, {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    requests: [
      {
        insertText: {
          location: { index: 1 },
          text: 'Hello from Google Docs API!'
        }
      }
    ]
  })
});
const result = await updateResponse.json();

// Read document content
const readResponse = await fetch(`https://docs.googleapis.com/v1/documents/${newDoc.documentId}`, {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const doc = await readResponse.json();

// List all task lists
const listsResponse = await fetch('https://tasks.googleapis.com/tasks/v1/users/@me/lists', {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const taskLists = await listsResponse.json();

// Create a new task
const tasklistId = taskLists.items[0].id;
const createResponse = await fetch(`https://tasks.googleapis.com/tasks/v1/lists/${tasklistId}/tasks`, {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    title: 'Complete project proposal',
    notes: 'Include budget and timeline',
    due: '2025-11-30T00:00:00.000Z'
  })
});
const newTask = await createResponse.json();

// List tasks
const tasksResponse = await fetch(`https://tasks.googleapis.com/tasks/v1/lists/${tasklistId}/tasks`, {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const tasks = await tasksResponse.json();

// Send a message to a Google Chat space
const spaceId = 'spaces/AAAAA';
const response = await fetch(`https://chat.googleapis.com/v1/${spaceId}/messages`, {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    text: 'Hello from Google Chat API!'
  })
});
const message = await response.json();

// Send a card message with button
const cardResponse = await fetch(`https://chat.googleapis.com/v1/${spaceId}/messages`, {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    cardsV2: [{
      card: {
        header: { title: 'Task Update' },
        sections: [{
          widgets: [{
            textParagraph: { text: 'Your task is complete!' }
          }]
        }]
      }
    }]
  })
});
const cardMessage = await cardResponse.json();

// List users in domain
const response = await fetch('https://admin.googleapis.com/admin/directory/v1/users?customer=my_customer&maxResults=10', {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const users = await response.json();

// Create a new user
const createResponse = await fetch('https://admin.googleapis.com/admin/directory/v1/users', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    primaryEmail: 'newuser@example.com',
    name: {
      givenName: 'New',
      familyName: 'User'
    },
    password: 'SecurePassword123!'
  })
});
const newUser = await createResponse.json();

// List groups
const groupsResponse = await fetch('https://admin.googleapis.com/admin/directory/v1/groups?customer=my_customer', {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const groups = await groupsResponse.json();

// Create a new meeting space
const response = await fetch('https://meet.googleapis.com/v2/spaces', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    config: {
      accessType: 'OPEN',
      entryPointAccess: 'ALL'
    }
  })
});
const meeting = await response.json();
// meeting.meetingUri contains the join URL
// meeting.meetingCode contains the meeting code

// Get meeting details
const detailsResponse = await fetch(`https://meet.googleapis.com/v2/${meeting.name}`, {
  headers: {
    'Authorization': `Bearer ${accessToken}`
  }
});
const details = await detailsResponse.json();

// Create a new presentation
const createResponse = await fetch('https://slides.googleapis.com/v1/presentations', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    title: 'My Presentation'
  })
});
const presentation = await createResponse.json();

// Add a slide with text
const slideId = 'slide_' + Date.now();
const textBoxId = 'textbox_' + Date.now();
const updateResponse = await fetch(`https://slides.googleapis.com/v1/presentations/${presentation.presentationId}:batchUpdate`, {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    requests: [
      { createSlide: { objectId: slideId } },
      {
        createShape: {
          objectId: textBoxId,
          shapeType: 'TEXT_BOX',
          elementProperties: {
            pageObjectId: slideId,
            size: { width: { magnitude: 300, unit: 'PT' }, height: { magnitude: 50, unit: 'PT' } },
            transform: { scaleX: 1, scaleY: 1, translateX: 100, translateY: 100, unit: 'PT' }
          }
        }
      },
      {
        insertText: {
          objectId: textBoxId,
          text: 'Hello from Slides API!'
        }
      }
    ]
  })
});
const result = await updateResponse.json();

// Create a new form
const createResponse = await fetch('https://forms.googleapis.com/v1/forms', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    info: {
      title: 'Customer Feedback Form'
    }
  })
});
const form = await createResponse.json();

// Add a question to the form
const updateResponse = await fetch(`https://forms.googleapis.com/v1/forms/${form.formId}:batchUpdate`, {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    requests: [{
      createItem: {
        item: {
          title: 'What is your email?',
          questionItem: {
            question: {
              required: true,
              textQuestion: { paragraph: false }
            }
          }
        },
        location: { index: 0 }
      }
    }]
  })
});
const result = await updateResponse.json();

// Get form responses
const responsesResponse = await fetch(`https://forms.googleapis.com/v1/forms/${form.formId}/responses`, {
  headers: { 'Authorization': `Bearer ${accessToken}` }
});
const responses = await responsesResponse.json();
