// Query and manage tabs
chrome.tabs.query({}, (tabs) => {
  tabs.forEach(tab => {
    console.log(tab.title, tab.url);
  });
});

// Group tabs by domain
chrome.tabs.query({}, async (tabs) => {
  const groupId = await chrome.tabs.group({ tabIds: tabs.map(t => t.id) });
  await chrome.tabGroups.update(groupId, { title: "My Group" });
});

// Close duplicate tabs
chrome.tabs.query({}, (tabs) => {
  const urls = new Set();
  tabs.forEach(tab => {
    if (urls.has(tab.url)) {
      chrome.tabs.remove(tab.id);
    } else {
      urls.add(tab.url);
    }
  });
});

// Get all bookmarks
chrome.bookmarks.getTree((bookmarkTree) => {
  console.log(bookmarkTree);
});

// Search bookmarks
chrome.bookmarks.search("keyword", (results) => {
  results.forEach(bookmark => {
    console.log(bookmark.title, bookmark.url);
  });
});

// Create new bookmark
chrome.bookmarks.create({
  parentId: "1",
  title: "My Site",
  url: "https://example.com"
});

// Listen for bookmark changes
chrome.bookmarks.onCreated.addListener((id, bookmark) => {
  console.log("New bookmark:", bookmark.title);
});

// content.js - Inject custom CSS
const style = document.createElement('style');
style.textContent = `
  body { background-color: #1e1e1e !important; color: #fff !important; }
  * { background-color: #1e1e1e !important; color: #fff !important; }
`;
document.head.appendChild(style);

// Remove elements
document.querySelectorAll('.ad, .banner').forEach(el => el.remove());

// Modify existing elements
document.querySelectorAll('p').forEach(p => {
  p.style.fontSize = '18px';
  p.style.lineHeight = '1.6';
});

// background.js
chrome.runtime.onInstalled.addListener(() => {
  chrome.contextMenus.create({
    id: "highlightAction",
    title: "Process selected text",
    contexts: ["selection"]
  });
});

// Handle context menu clicks
chrome.contextMenus.onClicked.addListener((info, tab) => {
  if (info.menuItemId === "highlightAction") {
    const selectedText = info.selectionText;
    // Perform action with selectedText
    console.log("Selected:", selectedText);
  }
});

// content.js - Add video controls
const videos = document.querySelectorAll('video');

videos.forEach(video => {
  // Add speed control
  video.playbackRate = 1.5;

  // Add keyboard shortcuts
  document.addEventListener('keydown', (e) => {
    if (e.key === 'ArrowRight') video.currentTime += 5;
    if (e.key === 'ArrowLeft') video.currentTime -= 5;
    if (e.key === ' ') video.paused ? video.play() : video.pause();
  });

  // Capture screenshot
  const canvas = document.createElement('canvas');
  canvas.width = video.videoWidth;
  canvas.height = video.videoHeight;
  canvas.getContext('2d').drawImage(video, 0, 0);
  const screenshot = canvas.toDataURL('image/png');
});

// content.js - Filter and enhance social feed
const observer = new MutationObserver((mutations) => {
  const posts = document.querySelectorAll('[data-testid="post"]');

  posts.forEach(post => {
    const text = post.innerText.toLowerCase();

    // Filter out posts with keywords
    if (text.includes('sponsored') || text.includes('ad')) {
      post.style.display = 'none';
    }

    // Add download button
    if (!post.querySelector('.download-btn')) {
      const btn = document.createElement('button');
      btn.textContent = 'Download';
      btn.className = 'download-btn';
      post.appendChild(btn);
    }
  });
});

observer.observe(document.body, { childList: true, subtree: true });

// background.js - Track time on sites
let activeTabId = null;
let startTime = Date.now();

chrome.tabs.onActivated.addListener(async (activeInfo) => {
  // Save time for previous tab
  if (activeTabId) {
    const timeSpent = Date.now() - startTime;
    const tab = await chrome.tabs.get(activeTabId);
    const domain = new URL(tab.url).hostname;

    chrome.storage.local.get([domain], (result) => {
      const totalTime = (result[domain] || 0) + timeSpent;
      chrome.storage.local.set({ [domain]: totalTime });
    });
  }

  activeTabId = activeInfo.tabId;
  startTime = Date.now();
});

// content.js - Auto-fill form fields
const formData = {
  name: 'John Doe',
  email: 'john@example.com',
  phone: '555-0123'
};

// Fill inputs by type
document.querySelectorAll('input').forEach(input => {
  const type = input.type.toLowerCase();
  const name = input.name.toLowerCase();

  if (type === 'email' || name.includes('email')) {
    input.value = formData.email;
  } else if (type === 'tel' || name.includes('phone')) {
    input.value = formData.phone;
  } else if (name.includes('name')) {
    input.value = formData.name;
  }

  input.dispatchEvent(new Event('input', { bubbles: true }));
});

// content.js - Extract data from page
function extractData() {
  const data = [];

  // Extract from table
  document.querySelectorAll('table tr').forEach(row => {
    const cells = Array.from(row.querySelectorAll('td, th'));
    data.push(cells.map(cell => cell.innerText.trim()));
  });

  // Extract products
  const products = [];
  document.querySelectorAll('.product').forEach(product => {
    products.push({
      title: product.querySelector('.title')?.innerText,
      price: product.querySelector('.price')?.innerText,
      image: product.querySelector('img')?.src
    });
  });

  return { tableData: data, products };
}

// Send to background
chrome.runtime.sendMessage({ action: 'saveData', data: extractData() });

// background.js - Create notifications
chrome.runtime.onInstalled.addListener(() => {
  // Create alarm for periodic notifications
  chrome.alarms.create('reminder', { periodInMinutes: 30 });
});

chrome.alarms.onAlarm.addListener((alarm) => {
  if (alarm.name === 'reminder') {
    chrome.notifications.create({
      type: 'basic',
      iconUrl: 'icon.png',
      title: 'Reminder',
      message: 'Time to take a break!',
      priority: 2
    });
  }
});

// Create notification on event
chrome.notifications.create({
  type: 'basic',
  iconUrl: 'icon.png',
  title: 'New Event',
  message: 'Something happened!'
});

// background.js - Store credentials securely
chrome.runtime.onMessage.addListener((request, sender, sendResponse) => {
  if (request.action === 'saveCredentials') {
    chrome.storage.local.set({
      [`creds_${request.domain}`]: {
        username: request.username,
        encrypted: request.encryptedPassword
      }
    });
  }

  if (request.action === 'getCredentials') {
    chrome.storage.local.get([`creds_${request.domain}`], (result) => {
      sendResponse(result[`creds_${request.domain}`]);
    });
    return true;
  }
});

// content.js - Auto-fill login
chrome.storage.local.get([`creds_${window.location.hostname}`], (result) => {
  if (result) {
    document.querySelector('input[type="email"]').value = result.username;
  }
});

// content.js - Track prices
function extractProductInfo() {
  const title = document.querySelector('h1')?.innerText;
  const price = document.querySelector('.price, [data-price]')?.innerText;
  const image = document.querySelector('.product-image')?.src;

  return { title, price, image, url: window.location.href };
}

// Save price history
const product = extractProductInfo();
chrome.storage.local.get(['priceHistory'], (result) => {
  const history = result.priceHistory || [];
  history.push({ ...product, timestamp: Date.now() });
  chrome.storage.local.set({ priceHistory: history });
});

// Check for price drop
const previousPrice = 99.99;
const currentPrice = parseFloat(product.price.replace(/[^0-9.]/g, ''));
if (currentPrice < previousPrice) {
  chrome.runtime.sendMessage({ action: 'priceAlert', product, drop: previousPrice - currentPrice });
}

// background.js - Block requests (Manifest V3)
chrome.declarativeNetRequest.updateDynamicRules({
  removeRuleIds: [1, 2, 3],
  addRules: [
    {
      id: 1,
      priority: 1,
      action: { type: "block" },
      condition: {
        urlFilter: "*ads*",
        resourceTypes: ["script", "image", "xmlhttprequest"]
      }
    },
    {
      id: 2,
      priority: 1,
      action: { type: "block" },
      condition: {
        urlFilter: "*tracker*|*analytics*|*doubleclick*",
        resourceTypes: ["script"]
      }
    }
  ]
});

// content.js - Remove elements
const selectors = [
  '.ad', '.advertisement', '[class*="cookie"]',
  '.paywall', '.subscribe-banner', '.popup-overlay'
];

const observer = new MutationObserver(() => {
  selectors.forEach(selector => {
    document.querySelectorAll(selector).forEach(el => el.remove());
  });
});

observer.observe(document.body, { childList: true, subtree: true });

// background.js - Monitor page changes
chrome.alarms.create('checkPage', { periodInMinutes: 5 });

chrome.alarms.onAlarm.addListener(async (alarm) => {
  if (alarm.name === 'checkPage') {
    const [tab] = await chrome.tabs.query({ url: 'https://example.com/*' });

    if (tab) {
      const result = await chrome.scripting.executeScript({
        target: { tabId: tab.id },
        func: () => document.body.innerText
      });

      const currentContent = result[0].result;

      chrome.storage.local.get(['lastContent'], (data) => {
        if (data.lastContent && data.lastContent !== currentContent) {
          chrome.notifications.create({
            type: 'basic',
            iconUrl: 'icon.png',
            title: 'Page Changed',
            message: 'The monitored page has been updated'
          });
        }
        chrome.storage.local.set({ lastContent: currentContent });
      });
    }
  }
});

// background.js - Create multiple context menus
chrome.runtime.onInstalled.addListener(() => {
  // For selected text
  chrome.contextMenus.create({
    id: "searchText",
    title: "Search '%s'",
    contexts: ["selection"]
  });

  // For links
  chrome.contextMenus.create({
    id: "copyLink",
    title: "Copy link address",
    contexts: ["link"]
  });

  // For images
  chrome.contextMenus.create({
    id: "saveImage",
    title: "Save image as...",
    contexts: ["image"]
  });
});

chrome.contextMenus.onClicked.addListener((info, tab) => {
  if (info.menuItemId === "searchText") {
    chrome.tabs.create({ url: `https://google.com/search?q=${info.selectionText}` });
  }
});
