// Authenticate and list recent emails
gapi.client.gmail.users.messages.list({
  'userId': 'me',
  'maxResults': 10
}).then(response => {
  const messages = response.result.messages;
  messages.forEach(message => {
    // Get full message details
    gapi.client.gmail.users.messages.get({
      'userId': 'me',
      'id': message.id
    }).then(msg => {
      console.log('Subject:', msg.result.payload.headers.find(h => h.name === 'Subject').value);
    });
  });
});

// Send an email
const email = [
  'To: recipient@example.com',
  'Subject: Hello from Extension',
  '',
  'This is the email body.'
].join('\n');

const encodedEmail = btoa(email).replace(/\+/g, '-').replace(/\//g, '_');
gapi.client.gmail.users.messages.send({
  'userId': 'me',
  'resource': {
    'raw': encodedEmail
  }
});

// List files in Drive
gapi.client.drive.files.list({
  'pageSize': 10,
  'fields': 'files(id, name, mimeType, modifiedTime)'
}).then(response => {
  const files = response.result.files;
  files.forEach(file => {
    console.log(`${file.name} (${file.id})`);
  });
});

// Upload a file
const metadata = {
  name: 'my-document.txt',
  mimeType: 'text/plain'
};

const fileContent = 'Hello, World!';
const file = new Blob([fileContent], {type: 'text/plain'});

const form = new FormData();
form.append('metadata', new Blob([JSON.stringify(metadata)], {type: 'application/json'}));
form.append('file', file);

fetch('https://www.googleapis.com/upload/drive/v3/files?uploadType=multipart', {
  method: 'POST',
  headers: new Headers({'Authorization': 'Bearer ' + accessToken}),
  body: form
}).then(response => response.json())
  .then(data => console.log('File created:', data.id));

// List upcoming events
gapi.client.calendar.events.list({
  'calendarId': 'primary',
  'timeMin': (new Date()).toISOString(),
  'maxResults': 10,
  'singleEvents': true,
  'orderBy': 'startTime'
}).then(response => {
  const events = response.result.items;
  events.forEach(event => {
    const start = event.start.dateTime || event.start.date;
    console.log(`${event.summary} - ${start}`);
  });
});

// Create a new event
const event = {
  'summary': 'Team Meeting',
  'location': 'Conference Room A',
  'description': 'Weekly team sync',
  'start': {
    'dateTime': '2025-10-15T10:00:00-07:00',
    'timeZone': 'America/Los_Angeles'
  },
  'end': {
    'dateTime': '2025-10-15T11:00:00-07:00',
    'timeZone': 'America/Los_Angeles'
  },
  'attendees': [
    {'email': 'colleague@example.com'}
  ],
  'reminders': {
    'useDefault': false,
    'overrides': [
      {'method': 'email', 'minutes': 24 * 60},
      {'method': 'popup', 'minutes': 10}
    ]
  }
};

gapi.client.calendar.events.insert({
  'calendarId': 'primary',
  'resource': event
}).then(event => {
  console.log('Event created:', event.result.htmlLink);
});

// Create a new document
gapi.client.docs.documents.create({
  'resource': {
    'title': 'My New Document'
  }
}).then(response => {
  const documentId = response.result.documentId;
  console.log('Created document:', documentId);

  // Insert text into the document
  const requests = [
    {
      insertText: {
        location: {
          index: 1
        },
        text: 'Hello, this is my document content!\n'
      }
    },
    {
      updateParagraphStyle: {
        range: {
          startIndex: 1,
          endIndex: 40
        },
        paragraphStyle: {
          namedStyleType: 'HEADING_1'
        },
        fields: 'namedStyleType'
      }
    }
  ];

  return gapi.client.docs.documents.batchUpdate({
    'documentId': documentId,
    'resource': {
      'requests': requests
    }
  });
});

// Read document content
gapi.client.docs.documents.get({
  'documentId': documentId
}).then(response => {
  const doc = response.result;
  console.log('Document title:', doc.title);
  console.log('Content:', doc.body.content);
});

// Read values from a spreadsheet
gapi.client.sheets.spreadsheets.values.get({
  'spreadsheetId': spreadsheetId,
  'range': 'Sheet1!A1:D10'
}).then(response => {
  const values = response.result.values;
  values.forEach(row => {
    console.log(row.join(', '));
  });
});

// Write values to a spreadsheet
const values = [
  ['Name', 'Age', 'Email'],
  ['John Doe', 30, 'john@example.com'],
  ['Jane Smith', 25, 'jane@example.com']
];

gapi.client.sheets.spreadsheets.values.update({
  'spreadsheetId': spreadsheetId,
  'range': 'Sheet1!A1:C3',
  'valueInputOption': 'RAW',
  'resource': {
    'values': values
  }
}).then(response => {
  console.log('Updated cells:', response.result.updatedCells);
});

// Create a new spreadsheet
gapi.client.sheets.spreadsheets.create({
  'resource': {
    'properties': {
      'title': 'My Data Sheet'
    },
    'sheets': [
      {
        'properties': {
          'title': 'Data',
          'gridProperties': {
            'rowCount': 100,
            'columnCount': 10
          }
        }
      }
    ]
  }
}).then(response => {
  console.log('Created spreadsheet:', response.result.spreadsheetId);
});

// Create a new form
const form = {
  info: {
    title: 'Customer Feedback Survey',
    documentTitle: 'Feedback Form'
  }
};

gapi.client.forms.forms.create({
  resource: form
}).then(response => {
  const formId = response.result.formId;
  console.log('Form created:', formId);

  // Add questions to the form
  const updates = {
    requests: [
      {
        createItem: {
          item: {
            title: 'How satisfied are you?',
            questionItem: {
              question: {
                required: true,
                choiceQuestion: {
                  type: 'RADIO',
                  options: [
                    {value: 'Very Satisfied'},
                    {value: 'Satisfied'},
                    {value: 'Neutral'},
                    {value: 'Dissatisfied'}
                  ]
                }
              }
            }
          },
          location: {index: 0}
        }
      }
    ]
  };

  return gapi.client.forms.forms.batchUpdate({
    formId: formId,
    resource: updates
  });
});

// Get form responses
gapi.client.forms.forms.responses.list({
  formId: formId
}).then(response => {
  const responses = response.result.responses;
  responses.forEach(r => {
    console.log('Response:', r.answers);
  });
});

// Create a new presentation
gapi.client.slides.presentations.create({
  resource: {
    title: 'Sales Report Q4 2025'
  }
}).then(response => {
  const presentationId = response.result.presentationId;
  console.log('Created presentation:', presentationId);

  // Add a slide with text
  const requests = [
    {
      createSlide: {
        slideLayoutReference: {
          predefinedLayout: 'TITLE_AND_BODY'
        }
      }
    },
    {
      insertText: {
        objectId: slideId,
        text: 'Q4 Sales Performance',
        insertionIndex: 0
      }
    }
  ];

  return gapi.client.slides.presentations.batchUpdate({
    presentationId: presentationId,
    resource: {requests: requests}
  });
});

// Read presentation content
gapi.client.slides.presentations.get({
  presentationId: presentationId
}).then(response => {
  const slides = response.result.slides;
  console.log('Slide count:', slides.length);
});

// List task lists
gapi.client.tasks.tasklists.list().then(response => {
  const lists = response.result.items;
  lists.forEach(list => {
    console.log('Task list:', list.title);
  });
});

// List tasks from a list
gapi.client.tasks.tasks.list({
  tasklist: taskListId
}).then(response => {
  const tasks = response.result.items;
  tasks.forEach(task => {
    console.log(`${task.title} - ${task.status}`);
