  title: 'Complete project report',
  notes: 'Finalize Q4 analysis',
  due: '2025-10-20T12:00:00.000Z'
};

gapi.client.tasks.tasks.insert({
  tasklist: taskListId,
  resource: task
}).then(response => {
  console.log('Task created:', response.result.id);
});

// Update task status
gapi.client.tasks.tasks.update({
  tasklist: taskListId,
  task: taskId,
  resource: {
    status: 'completed'
  }
});

// Get user's own profile
gapi.client.people.people.get({
  resourceName: 'people/me',
