  cards: [
    {
      header: {
        title: 'Extension Update',
        subtitle: 'New feature available'
      },
      sections: [
        {
          widgets: [
            {
              textParagraph: {
                text: 'Check out the new dashboard feature!'
              }
            },
            {
              buttons: [
                {
                  textButton: {
                    text: 'VIEW DETAILS',
                    onClick: {
                      openLink: {
                        url: 'https://example.com'
                      }
                    }
                  }
                }
              ]
            }
          ]
        }
      ]
