// Google Workspace Authentication with proper error handling
async function authenticateGoogleWorkspace() {
  return new Promise((resolve, reject) => {
    chrome.identity.getAuthToken({interactive: true}, (token) => {
      if (chrome.runtime.lastError) {
        const error = chrome.runtime.lastError.message;

        // Handle specific error cases
        if (error.includes('OAuth2 client ID')) {
          reject(new Error('âš ï¸ Setup Required: Please configure your OAuth Client ID in manifest.json. See setup instructions.'));
        } else if (error.includes('User did not approve')) {
          reject(new Error('Authentication cancelled by user'));
        } else {
          reject(new Error(`Authentication failed: ${error}`));
        }
        return;
      }

      if (!token) {
        reject(new Error('No authentication token received'));
        return;
      }

      resolve(token);
    });
  });
}

// Usage example with full error handling
async function fetchGmailMessages() {
  try {
    // Get authentication token
    const token = await authenticateGoogleWorkspace();

    // Make API request
    const response = await fetch('https://www.googleapis.com/gmail/v1/users/me/messages?maxResults=10', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    if (!response.ok) {
      if (response.status === 401) {
        // Token expired, clear cache and retry
        chrome.identity.removeCachedAuthToken({token: token}, async () => {
          // Retry authentication
          const newToken = await authenticateGoogleWorkspace();
          // Retry API call with new token
        });
      }
      throw new Error(`API request failed: ${response.statusText}`);
    }

    const data = await response.json();
    return data.messages || [];

  } catch (error) {
    console.error('Error fetching Gmail messages:', error);
    // Show user-friendly error message
    displayError(error.message);
    throw error;
  }
}

// Sign out function
function signOutGoogle() {
  chrome.identity.getAuthToken({interactive: false}, (token) => {
    if (token) {
      chrome.identity.removeCachedAuthToken({token: token}, () => {
        // Revoke token on Google's end
        fetch(`https://accounts.google.com/o/oauth2/revoke?token=${token}`);
        console.log('Signed out successfully');
      });
    }
  });
}

chrome_extension_setup.authentication_code:
// Get OAuth token
chrome.identity.getAuthToken({interactive: true}, (token) => {
  if (chrome.runtime.lastError) {
    console.error(chrome.runtime.lastError);
    return;
  }

  // Use token for API requests
  fetch('https://www.googleapis.com/gmail/v1/users/me/messages', {
    headers: {
      'Authorization': 'Bearer ' + token
    }
  }).then(response => response.json())
    .then(data => console.log(data));
});

// Remove cached token (for logout)
chrome.identity.removeCachedAuthToken({token: token}, () => {
  console.log('Token removed');
});

